import {oneLine, stripIndents} from 'common-tags'

import {renderBloomLogo} from './renderBloomLogo'
import {LargeButtonType} from './../../types'

/* tslint:disable:max-line-length */
const backgroundPattern =
  'data:image/png;base64,'
/* tslint:enable:max-line-length */

const renderStyle = (id: string, type: LargeButtonType) => {
  const style = document.createElement('style')

  let styleText = stripIndents(oneLine)`
    #${id} {
      background-image: url(${backgroundPattern}), linear-gradient(to right, #7A7CF3, #6262F6);
      background-repeat: no-repeat;
      background-position: left 39px center, left center;
      background-size: 282px 100%, 100% 100%;
      box-shadow: 0 4px 15px 0 rgba(98,98,246,0.50);
      color: #fff;
      text-decoration: none;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 4px;
      padding: 12px 39px;
      box-sizing: border-box;
    }

    #${id}-lock-container {
      display: inline-flex;
      flex-shrink: 0;
      align-items: center;
      justify-content: center;
      height: 24px;
      width: 24px;
      margin-left: -24px;
    }

    #${id}-text-and-logo {
      display: flex;
      width: 100%;
      align-items: center;
      justify-content: center;
      margin-left: 12px;
      margin-right: 12px;
    }

    #${id}-logo {
      margin-right: 8px;
    }
  `

  if (type === 'sign-up' || type === 'log-in' || type === 'verify') {
    styleText += stripIndents(oneLine)`
      #${id}-text {
        margin-top: 2px;
      }
    `
  }

  style.append(styleText)

  return style
}

